// eslint-disable-next-line no-unused-vars
import React from 'react'
import Card from './Card'

function Datascience() {
  let data = [
    {
      image : "data:image/jpeg;base64,",
      title  : "What is the Difference between Data Science and Data Engineering?",
      description : "Data engineers focus on managing and organizing data, building and maintaining databases and data pipelines, while data scientists focus on analyzing and interpreting data to find insights and patterns."
    },
    {
      image : "data:image/jpeg;base64,",
      title  : "Top 10 Data Science Tools in 2024",
      description : "ML enthusiasts find ease in TensorFlow (55%) and PyTorch (50%) for cutting-edge model development."
    }
  ]
  return <>
    <div className="container mt-5">
      <div className="row">
        {
          data.map((e,i)=>{
            return <Card cardData={e} key={i}/>
          })
        }
      </div>
    </div>
  </>
}

export default Datascience